CREATE TABLE `proveedores` (`id` int(11) NOT NULL  AUTO_INCREMENT  , `nit` varchar(255) NOT NULL  , `empresa` varchar(255) NOT NULL  , `nombre` text NOT NULL  , `telefono` varchar(255) NOT NULL  , `direccion` varchar(255) NOT NULL  , `correo` varchar(255) NOT NULL  , `pedido` varchar(255) NOT NULL  , `observacion` text NOT NULL  , PRIMARY KEY (`id`))ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin;